mapper.update:
update sys_practice_scope
set practice_type_id = #{practiceTypeId,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},
  code = #{code,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, practice_type_id, name, code
from sys_practice_scope
where id = #{id,jdbcType=INTEGER}
